package loop;

public class Do_While2 {

	//
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=0;
		do
		{
			System.out.println(i);
			i=i+2;
		}while(i<=100);
		System.out.println();
		int j=100;
		do
		{
			System.out.println(j);
			j=j-2;
		}while(j>=0);
	}

}
