package loop;

public class Do_While3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int i=1;
		do
		{
			System.out.println(i);
			i=i+2;
		}while(i<=100);
		
		System.out.println();
		int j=99;
		do
		{
			System.out.println(j);
			j=j-2;
		}while(j>=1);
	}

}
