package loop;

public class Do_while1 {

	//2. Print no from 0 to 10
	public static void main(String[] args) {
		// TODO Auto-generated method stub
 
		int i=1;
		do
		{
			System.out.println(i);
			i++;
		}while(i<=10);
		System.out.println();
		//2. Print no from 10 to 0
		int j=10;
		do
		{
			System.out.println(j);
			j--;
		}while(j>=1);
	}

}
